<script>
export default {
    computed: {
        isDemoMode() {
            return this.$auth.loggedIn && this.$auth.user.demo_mode === 1;
        },
    },
    methods: {
        async logOut() {
            try {
                await this.$auth.logout();
                this.$router.push('/login');
            } catch (err) {}
        },
        async exitFromDemoMode() {
            const response = await this.$axios.post('/clients/exit-from-demo-mode');
            console.log(response.data.data.token);
            await this.$auth.setUserToken(response.data.data.token);
            window.location.reload();
        },
    },
};
</script>

